mapper.select:
SELECT fd.funding_idx, fd.member_idx, fd.funding_title, fd.funding_thumbnail, fd.funding_target_price, fd.funding_current_price, fd.funding_start_date, fd.funding_end_date, fd.funding_permit_state, fd.funding_current_state, fd.funding_category, fd.funding_views, mem.member_business_name, mem.member_idx FROM funding AS fd JOIN member AS mem
ON fd.funding_category = 0 AND fd.member_idx = mem.member_idx AND funding_current_state=1
ON fd.funding_category = 1 AND fd.member_idx = mem.member_idx AND funding_current_state=1
ON fd.funding_category = 2 AND fd.member_idx = mem.member_idx AND funding_current_state=1
ON fd.funding_category = 0 AND fd.member_idx = mem.member_idx AND funding_current_state=1
order by funding_idx desc limit #{startRowNum}, 9;
order by funding_views desc limit #{startRowNum}, 9;
order by funding_current_price desc limit #{startRowNum}, 9;
order by funding_idx desc limit #{startRowNum}, 9;

SELECT COUNT(funding_idx)
FROM funding


		where funding_category=0 AND funding_current_state=1;


		where funding_category=1 AND funding_current_state=1;


		where funding_category=2 AND funding_current_state=1;


		where funding_category=0 AND funding_current_state=1;

SELECT COUNT(funding_order_idx)
FROM funding_order
WHERE funding_idx= #{funding_idx}

mapper.update:
UPDATE funding SET
funding_views = funding_views+1
WHERE funding_idx = #{funding_idx}

UPDATE funding_detail_community 
SET funding_detail_community_content = #{funding_detail_community_content}, funding_detail_community_category = #{funding_detail_community_category}
WHERE funding_idx = #{funding_idx} and member_idx = #{member_idx};

mapper.select:
SELECT COUNT(funding_detail_community_idx)
FROM funding_detail_community
where funding_idx=#{funding_idx};

mapper.update:
UPDATE funding_qna 
SET funding_qna_state = 1
WHERE funding_idx = #{funding_idx} and funding_qna_idx = #{funding_qna_idx};

UPDATE funding_qna 
SET funding_qna_content = #{funding_qna_content}, funding_qna_secret = #{funding_qna_secret}
WHERE funding_qna_idx = #{funding_qna_idx};

mapper.delete:
DELETE FROM funding_qna

	WHERE funding_qna_idx = #{funding_qna_idx} OR parent_id = #{funding_qna_idx}; 


	WHERE funding_qna_idx = #{funding_qna_idx};

mapper.select:
SELECT member_idx
FROM zzim
WHERE funding_idx = #{funding_idx} AND member_idx = #{member_idx};

mapper.insert:
INSERT INTO funding_order(
	funding_order_total_price
  , funding_order_pay_state
  , funding_order_date
  , member_idx
  , funding_idx
)VALUES(
	#{funding_order_total_price}
  , #{funding_order_pay_state}
  , now()
  , #{member_idx}
  , #{funding_idx}
)

INSERT INTO funding_order_option(
	funding_order_idx
  , funding_order_option_select_idx
  , funding_order_option_select_count
)VALUES(
	#{funding_order_idx}
  , #{funding_order_option_select_idx}
  , #{funding_order_option_select_count}
)
